declare type PropertySign = {
    /** 这只是个类型标志位，不要使用 */
    _propertySign: symbol;
};

declare type BattlePropertyKind = {
    /** 当前生命值 */
    hp: PropertySign;
    /** 当前怒气值 */
    energy: PropertySign;
    /** 生命值比例（生命值/最大生命值） */
    hpRate: PropertySign;
    /** 英雄位置(0-5号位) */
    heroIndex: PropertySign;
    /** 攻击 */
    attack: PropertySign;
    /** 防御 */
    armor: PropertySign;
    /** 最大生命值 */
    maxHp: PropertySign;
    /** 增伤 */
    damage: PropertySign;
    /** 减伤 */
    defence: PropertySign;
    /** 针对燃烧敌人的增伤 */
    damageForBurn: PropertySign;
    /** 命中 */
    hit: PropertySign;
    /** 闪避 */
    dodge: PropertySign;
    /** 暴击率 */
    critical: PropertySign;
    /** 反暴击 */
    criticalImmune: PropertySign;
    /** 暴击伤害 */
    criticalDamage: PropertySign;
    /** 暴击伤害抵抗 */
    criticalResistant: PropertySign;
    /** 移动速度 */
    moveSpeed: PropertySign;
};
/** 英雄属性类型 */
declare let property: BattlePropertyKind;
